sfully!"); ?>'        });      }).catch(err => {        console.error('خطأ أثناء النسخ:', err);      });    });  });});</script></body></html><?php /**PATH /home/city2tec/sami-care.sa/resources/views/components/frontend/auth/coupon.blade.php ENDPATH**/ ?>